mapper.update:
update bridge_tb set

    bridge_name = #{bridgeName},


    span = #{span},


    structure = #{structure},


    gmt_create = #{gmtCreate},


    struct_num = #{structNum}


    picture_num = #{picture}


    report_path = #{reportPath}


    src_path = #{srcPath}


    user_id = #{userId}


    status = #{status}


    quantified_path = #{quantifiedPath}


    detected_path = #{detectedPath}


where id = #{id}

mapper.select:
select

from bridge_tb b, user_tb u
where
b.user_id = u.id

   AND u.username like concat("%", #{username}, "%")


    AND b.user_id = #{userId}


    AND b.bridge_name like concat("%", #{bridgeName}, "%")


    AND b.span = #{span}


    AND b.structure like concat("%", #{structure}, "%")



    AND  gmt_create >= #{start,jdbcType=DATE}  


    AND  gmt_create <= #{end,jdbcType=DATE} 


    limit #{pageIndex}, #{pageSize}
